import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import "./Chief.css";
import * as bs from "bootstrap/dist/css/bootstrap.css";
import * as bst from "bootstrap/dist/css/bootstrap-theme.css";

export default function ChiefOfProject() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projects, setProjects] = useState([]);
  const [show, setShow] = useState(false);
  const history = useHistory();

  const validateForm = () => {
    return name.length > 0 && description.length > 0;
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProjects((projects) => [...projects, name]);
  };

  const addProtocols = async (event) => {
    event.preventDefault();
    history.push("/protocolconf");
    console.log("Agregar logica de agregar protocolos a un proyecto");
  };

  return (
    <div className="chief-body">
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <div class="project-create">
        <Form onSubmit={handleSubmit}>
          <h3>Crear un nuevo Proyecto</h3>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Nombre del Proyecto</Form.Label>
            <Form.Control
              autoFocus
              type="text"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="description">
            <Form.Label>Descripci√≥n del Proyecto</Form.Label>
            <Form.Control
              autoFocus
              type="text"
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>
          <div>
            <Button block size="lg" type="submit" disabled={!validateForm()}>
              Guardar
            </Button>
          </div>
        </Form>
      </div>
      <div class="project-list">
        <h3>Proyectos creados</h3>
        {projects.length === 0 && <div>No hay proyectos creados</div>}
        {projects.length > 0 &&
          projects.map((project) => (
            <div class="protocol">
              <p>Nombre: {project}</p>
              <Button variant="primary" size="sm" onClick={handleShow}>
                Agregar Protocolos
              </Button>
            </div>
          ))}
      </div>
    </div>
  );
}
