import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./Login.css";
import appLogin from "../service/login-services";
import ChiefOfProject from "./chief-of-project";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [hasToken, setHasToken] = useState(false);
  const [jobTitle, setJobTitle] = useState("");

  const validateForm = () => {
    return username.length > 0 && password.length > 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await appLogin(username, password);
    setHasToken(response.data.token);
    setJobTitle(response.data.currentUser[0].job_title);
  };

  return (
    <>
      {!hasToken && (
        <div className="Login">
          <Form onSubmit={handleSubmit}>
            <Form.Group size="lg" controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                autoFocus
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>
            <Form.Group size="lg" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <div>
              <Button block size="lg" type="submit" disabled={!validateForm()}>
                Login
              </Button>
            </div>
          </Form>
        </div>
      )}
      {jobTitle === "Jefe de Proyecto" && <ChiefOfProject />}
    </>
  );
}
