import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./Protocol.css";

export default function Protocol() {
  //Debemos recibir el id de proyecto
  const [name, setName] = useState("");
  const [order, setOrder] = useState(0);
  const [isLocal, setIsLocal] = useState(false);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const validateForm = () => {
    return name.length > 0 && order.length > 0 && startDate && endDate;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log("Crea nuevo protocolo");
  };

  return (
    <div class="protocol-body">
      <Form onSubmit={handleSubmit}>
        <Form.Group size="lg" controlId="name">
          <Form.Label>Nombre del Protocolo</Form.Label>
          <Form.Control
            autoFocus
            type="text"
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="order">
          <Form.Label>Orden del protocolo</Form.Label>
          <Form.Control
            autoFocus
            type="number"
            min="0"
            onChange={(e) => setOrder(e.target.value)}
          />
        </Form.Group>
        <div id="formGridCheckbox" controlId="order" class="checkbox-form">
          <Form.Label>Protocolo local</Form.Label>
          <Form.Control
            type="checkbox"
            onChange={(e) => setIsLocal(e.target.checked)}
            value="isLocal"
          />
        </div>
        <Form.Group size="lg" controlId="startDate">
          <Form.Label>Fecha de incicio</Form.Label>
          <Form.Control
            type="date"
            onChange={(e) => setStartDate(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group size="lg" controlId="endDate">
          <Form.Label>Fecha de fin</Form.Label>
          <Form.Control
            type="date"
            onChange={(e) => setEndDate(e.target.value)}
            required
          />
        </Form.Group>
        <div>
          <Button
            variant="dark"
            block
            size="sm"
            type="submit"
            disabled={!validateForm()}
          >
            Agregar
          </Button>
        </div>
      </Form>
    </div>
  );
}
