import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Protocol from "./protocol";
import "./Chief.css";

export default function ChiefOfProject() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projects, setProjects] = useState([]);
  const [show, setShow] = useState(false);
  const history = useHistory();

  const validateForm = () => {
    return name.length > 0 && description.length > 0;
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProjects((projects) => [...projects, name]);
  };

  const addProtocols = async (event) => {
    event.preventDefault();
    history.push("/protocolconf");
    console.log("Agregar logica de agregar protocolos a un proyecto");
  };

  return (
    <div className="chief-body">
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Agregar Protocolo</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Protocol />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Guardar Protocolo
          </Button>
        </Modal.Footer>
      </Modal>

      <Form onSubmit={handleSubmit}>
        <h3>Crear un nuevo Proyecto</h3>
        <Form.Group controlId="name">
          <Form.Label>Nombre</Form.Label>
          <Form.Control
            autoFocus
            type="text"
            onChange={(e) => setName(e.target.value)}
            placeholder="Nombre del proyecto"
          />
        </Form.Group>
        <Form.Group controlId="description">
          <Form.Label>Descripción</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>
        <Button variant="dark" type="submit" disabled={!validateForm()}>
          Guardar
        </Button>
      </Form>
      <div>
        <Card>
          <Card.Header>Featured</Card.Header>
          <Card.Body>
            <Card.Title>Special title treatment</Card.Title>
            <Card.Text>
              With supporting text below as a natural lead-in to additional
              content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
      </div>
      <div class="project-list">
        <h3>Proyectos creados</h3>
        {projects.length === 0 && <div>No hay proyectos creados</div>}
        {projects.length > 0 &&
          projects.map((project) => (
            <div class="protocol">
              <p>Nombre: {project}</p>
              <Button variant="dark" size="sm" onClick={handleShow}>
                Agregar Protocolos
              </Button>
            </div>
          ))}
      </div>
    </div>
  );
}
