import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./Login.css";
import { appLogin } from "../service/api-services";
import ChiefOfProject from "./chief-of-project";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [hasToken, setHasToken] = useState(false);
  const [jobTitle, setJobTitle] = useState("");
  const [error, setError] = useState("");

  const validateForm = () => {
    return username.length > 0 && password.length > 0;
  };

  const userDataStorage = (data) => {
    localStorage.setItem("token", data.token);
    localStorage.setItem("username", data.currentUser[0].userName);
    localStorage.setItem("userId", data.currentUser[0].id);
    localStorage.setItem("userRole", data.currentUser[0].job_title);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { data } = await appLogin(username, password);
    if (data) {
      setHasToken(data.token);
      setJobTitle(data.currentUser[0].job_title);
      userDataStorage(data);
    } else {
      setError("Usuario y/o contraseña incorrectos");
    }
  };

  return (
    <>
      {!hasToken && localStorage.token && (
        <div className="Login">
          <Form onSubmit={handleSubmit}>
            <Form.Group size="lg" controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                autoFocus
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>
            <Form.Group size="lg" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <div>
              <Button block size="lg" type="submit" disabled={!validateForm()}>
                Login
              </Button>
            </div>
            {error && <p>{error}</p>}
          </Form>
        </div>
      )}
      {jobTitle === "Jefe de Proyecto" && <ChiefOfProject />}
      {jobTitle === "Responsable de Protocolo" && <h1>Holi</h1>}
    </>
  );
}
