import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Form, Button, Modal, Card, Col, Row } from "react-bootstrap";

import Protocol from "./protocol";
import "./Chief.css";

export default function ChiefOfProject() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projects, setProjects] = useState([]);
  const [show, setShow] = useState(false);
  const history = useHistory();

  const validateForm = () => {
    return name.length > 0 && description.length > 0;
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProjects((projects) => [...projects, name]);
  };

  const handleShowProtocols = async (event) => {
    console.log("Agregar logica de agregar protocolos a un proyecto");
  };

  const handleStartProject = async (event) => {
    console.log("Agregar logica de agregar protocolos a un proyecto");
  };

  return (
    <div className="chief-body">
      <Form onSubmit={handleSubmit}>
        <h3>Crear un nuevo Proyecto</h3>
        <Form.Group controlId="name">
          <Form.Label>Nombre</Form.Label>
          <Form.Control
            autoFocus
            type="text"
            onChange={(e) => setName(e.target.value)}
            placeholder="Nombre del proyecto"
          />
        </Form.Group>
        <Form.Group controlId="description">
          <Form.Label>Descripci√≥n</Form.Label>
          <Form.Control
            type="text"
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>
        <Button variant="dark" type="submit" disabled={!validateForm()}>
          Guardar
        </Button>
      </Form>
      <div class="project-list">
        <Card>
          <Card.Header>Proyectos</Card.Header>
          {projects.length === 0 && (
            <Card.Body>
              <Card.Title>No hay proyectos creados</Card.Title>
            </Card.Body>
          )}
          {projects.length > 0 &&
            projects.map((project) => (
              <Card.Body>
                <Row>
                  <Col xs={8}>
                    <Card.Title>Nombre: {project}</Card.Title>
                  </Col>
                  <Col xs={2}>
                    <Button
                      variant="dark"
                      size="sm"
                      onClick={handleShowProtocols}
                    >
                      Ver Protocolos
                    </Button>
                  </Col>
                  <Col xs={2}>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={handleStartProject}
                    >
                      Iniciar Proyecto
                    </Button>
                  </Col>
                </Row>
              </Card.Body>
            ))}
        </Card>
      </div>
    </div>
  );
}
