import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default function ResponsibleOfProtocol() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [protocols, setProtocols] = useState([]);
  //const [id, setId] = useState("");

  const validateForm = () => {
    return name.length > 0 && description.length > 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    //setProjects((projects) => [...projects, name]);
    //const { data } = await getProcesses(localStorage.token);
    //console.log(data);
  };

  return (
    <div className="chief-body">
      <h3>Protocolos disponibles para ejecutar</h3>
      <div class="project-list">
        <Card>
          <Card.Header>Proyectos</Card.Header>
          {projects.length === 0 && (
            <Card.Body>
              <Card.Title>No hay proyectos creados</Card.Title>
            </Card.Body>
          )}
          {projects.length > 0 &&
            projects.map((project) => (
              <Card.Body>
                <Row>
                  <Col xs={8}>
                    <Card.Title>Nombre: {project}</Card.Title>
                  </Col>
                  <Col xs={2}>
                    <Button
                      variant="dark"
                      size="sm"
                      onClick={handleShowProtocols}
                    >
                      Ver Protocolos
                    </Button>
                  </Col>
                  <Col xs={2}>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={handleStartProject}
                    >
                      Iniciar Proyecto
                    </Button>
                  </Col>
                </Row>
              </Card.Body>
            ))}
        </Card>
      </div>
    </div>
  );
}
