import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Project from "./project";
import { advanceTask } from "../service/processes-services";
import "./Chief.css";

export default function ChiefOfProject() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [projects, setProjects] = useState([]);

  const validateForm = () => {
    return name.length > 0 && description.length > 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProjects((projects) => [...projects, name]);
  };

  return (
    <div className="chief-body">
      <div class="project-create">
        <Form onSubmit={handleSubmit}>
          <h3>Crear un nuevo Proyecto</h3>
          <Form.Group size="lg" controlId="name">
            <Form.Label>Nombre del Proyecto</Form.Label>
            <Form.Control
              autoFocus
              type="text"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="description">
            <Form.Label>Descripci√≥n del Proyecto</Form.Label>
            <Form.Control
              autoFocus
              type="text"
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>
          <div>
            <Button block size="lg" type="submit" disabled={!validateForm()}>
              Guardar
            </Button>
          </div>
        </Form>
      </div>
      <div class="project-list">
        <h3>Proyectos creados</h3>
        {projects.length === 0 && <div>No hay proyectos creados</div>}
        {projects.length > 0 &&
          projects.map((project) => (
            <div>
              <p>Nombre:{project}</p>
              <Button block size="sm" onClick={handleSubmit}>
                Agregar Protocolos
              </Button>
            </div>
          ))}
      </div>
    </div>
  );
}
