import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import DatePicker from "react-datepicker";
import "./Protocol.css";

export default function Protocol() {
  //Debemos recibir el id de proyecto
  const [name, setName] = useState("");
  const [order, setOrder] = useState(0);
  const [isLocal, setIsLocal] = useState(false);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const [description, setDescription] = useState("");

  const validateForm = () => {
    return name.length > 0 && order.length > 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log("Crea nuevo protocolo");
  };

  return (
    <div class="protocol-body">
      <Form onSubmit={handleSubmit}>
        <h3>Crear nuevo protocolo</h3>
        <Form.Group size="lg" controlId="name">
          <Form.Label>Nombre del Protocolo</Form.Label>
          <Form.Control
            autoFocus
            type="text"
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="order">
          <Form.Label>Orden del protocolo</Form.Label>
          <Form.Control
            autoFocus
            type="number"
            min="0"
            onChange={(e) => setOrder(e.target.value)}
          />
        </Form.Group>
        <div id="formGridCheckbox" controlId="order" class="checkbox-form">
          <label>Protocolo local</label>
          <input
            type="checkbox"
            onChange={(e) => setIsLocal(e.target.checked)}
            value="isLocal"
          />
        </div>
        <div className="form-section">
          <label htmlFor="startDate" className="form-label">
            Start Date
          </label>
          <DatePicker
            id="startDate"
            name="startDate"
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            minDate={new Date()}
            dateFormat="Pp"
            selectsStart
            startDate={startDate}
            endDate={endDate}
          />
        </div>
        <div className="form-section">
          <label htmlFor="endDate" className="form-label">
            End Date
          </label>
          <DatePicker
            id="endDate"
            name="endDate"
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            minDate={startDate || new Date()}
            dateFormat="Pp"
            selectsEnd
            startDate={startDate}
            endDate={endDate}
          />
        </div>
        <div>
          <Button block size="lg" type="submit" disabled={!validateForm()}>
            Agregar
          </Button>
        </div>
      </Form>
    </div>
  );
}
